import './style/Nav.css';
import logo from './images/logo.png';
import blurp from './images/blurp.png';

import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import Ingredients from "../Pages/Ingredients";

function Nav()
{
    const [name, setName] = useState("");

    return(
        <nav className='nav'>
            <Link to='/' className='logo-block'>
            <img className='logo' src={logo} alt='T_T'/>
            <img className='blurp' src={blurp} alt='T_T'/>
            </Link>
            <ul className='nav-list'>
                <li className='nav-item'><Link to='/'>Home</Link></li>
                <li className='nav-item'><Link to='/ingredients'>Ingredients</Link></li>
                <li className='nav-item'><Link to='/contacts'>Contacts</Link></li>
                <li className='nav-item'><Link to='/user'>User</Link></li>

            </ul>
            <form className='searchbar' action={`/recipes/search/${name}`} >
                <input className='search-text'
                       type='text'
                       placeholder='Search for a Recipe...'
                       value={name}
                       onChange={e => setName(e.target.value)}
                       required />
                <button type="submit" className='search-button'>
                    <i className="fa fa-search"></i>
                </button>
            </form>
        </nav>
    );
}

export default Nav;
